import { NavigationContainer } from "@react-navigation/native";
import TelaInicial from "./pages/telaInicial";
import LoginCliente from "./pages/loginCliente";
import CadastroCliente from "./pages/cadastroCliente";
import LoginTransportador from "./pages/loginTransportador";
import Home from "./pages/home";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { Image, View } from "react-native";
import { createStackNavigator } from "@react-navigation/stack";

const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

function HomeTabs() {
    return (
      <Tab.Navigator screenOptions={{headerShown: false, tabBarStyle: {backgroundColor: '#D8CEFB', borderTopStartRadius: 8, borderTopEndRadius: 8, height: 70, alignItems: '', display: 'flex', width: '100%', justifyContent: 'space-evenly'}}}>
            <Tab.Screen options={{tabBarLabelStyle: {fontSize: 12, fontWeight: 'bold', color: 'black'}, tabBarIcon: () => { return (<View><Image source={require('./assets/mapa.png')} style={{width: 40, height: 40}}></Image></View>)}}} name="Mapa" component={Home}/>
            <Tab.Screen options={{tabBarLabelStyle: {fontSize: 12, fontWeight: 'bold', color: 'black'}, tabBarIcon: () => { return (<View><Image source={require('./assets/pedidos.png')} style={{width: 40, height: 40}}></Image></View>)}}} name="Pedidos" component={Home}/>
            <Tab.Screen options={{tabBarLabelStyle: {fontSize: 12, fontWeight: 'bold', color: 'black', marginBottom: 10}, tabBarIcon: () => { return (<View><Image source={require('./assets/home.png')} style={{width: 70, height: 70, marginBottom: 20}}></Image></View>)}}} name="Home" component={Home}/>
            <Tab.Screen options={{tabBarLabelStyle: {fontSize: 12, fontWeight: 'bold', color: 'black'}, tabBarIcon: () => { return (<View><Image source={require('./assets/carrinho.png')} style={{width: 40, height: 40}}></Image></View>)}}} name="Carrinho" component={Home}/>
            <Tab.Screen options={{tabBarLabelStyle: {fontSize: 12, fontWeight: 'bold', color: 'black'}, tabBarIcon: () => { return (<View><Image source={require('./assets/perfil.png')} style={{width: 40, height: 40}}></Image></View>)}}} name="Perfil" component={Home}/>
      </Tab.Navigator>
    );
  }
  

export default function Routers({navigation}){
    return(
        <NavigationContainer>
            <Stack.Navigator screenOptions={{headerShown: false, tabBarStyle: {backgroundColor: '#D8CEFB', borderTopStartRadius: 8, borderTopEndRadius: 8, height: 64}}}>
                <Tab.Screen name="Home" component={HomeTabs}/>
                <Stack.Screen name="PÃ¡gina Inicial" component={TelaInicial}/>
                <Stack.Screen name="Login Cliente" component={LoginCliente}/>
                <Stack.Screen name="Cadastro Cliente" component={CadastroCliente}/>
                <Stack.Screen name="Login Transportador" component={LoginTransportador}/>
            </Stack.Navigator>

        </NavigationContainer>
    )
}
